const objLoad=o=>{let a=gsap.utils.toArray(o);a.forEach((function(){gsap.fromTo(a,{opacity:0,xPercent:-100},{duration:.6,opacity:1,xPercent:0,stagger:.1,ease:"power2.out"})}))},objLoadVertical=o=>{let a=gsap.utils.toArray(o);a.forEach((function(){gsap.fromTo(a,{opacity:0,xPercent:100},{duration:1.2,opacity:1,xPercent:0,stagger:.1,ease:"power2.out"})}))};objLoad(".about--image *"),objLoadVertical(".about--info *");